clear allclose all%create signal 1n1 = [0:10];signal1 = exp(-n1);subplot(4,2,1)stem(n1, signal1)title("Signal 1");xlabel("time");ylabel("amplitude");%create signal 2n2 = [0:20];signal2 = n2;subplot(4,2,3)stem(n2, signal2)title("Signal 2");xlabel("time");ylabel("amplitude");nconv = [n1(1) + n2(1):n1(end) + n2(end)];%convolution timeconvlength = length(nconv);%length of convolutionlen1 = length(signal1); %length of signal 1len2 = length(signal2); %length of signal 2X = [zeros(1,len2-1) signal1 zeros(1,len2-1)]; %zero paddingnX = n1(1) - (len2-1):n1(end) + (len2-1); %time for x with zerossubplot(4,2,2)stem(nX, X)title("Signal 1 with zeros");xlabel("time");ylabel("amplitude");signal2_reversed = signal2(end:-1:1);%create the reversed signal 2subplot(4,2,4)stem(n2, signal2_reversed)title("Signal 2 reversed");xlabel("time");ylabel("amplitude");%convolution using the typefor k=1:convlength    Y = [zeros(1,k-1) signal2_reversed zeros(1,convlength-k)];  Convsignal(k) = sum(X.*Y);  endsubplot(4,2,5:6)stem(nconv, Convsignal)title("Convolution without conv function");xlabel("time");ylabel("amplitude");%convolution with conv functionC = conv(signal1, signal2);subplot(4,2,7:8)stem(nconv, C)title("Convolution with conv function");xlabel("time");ylabel("amplitude");